using UnityEngine;
using System.Collections;
using UnityEngine.UI;
using System.Text.RegularExpressions;

public class TeamNamePopup : MonoBehaviour {

    public Text _inputTextField;
    public Text _inputField;
    public tk2dSlicedSprite iconX;
    public tk2dSlicedSprite iconCheck;

    private TouchScreenKeyboard _keyboard;

    public System.Action _callback;

    bool isTextCount = false;
    int _textCount = 0;
    string _textString;

    string _pastString = string.Empty;

    public string BanName;
    public void Init(System.Action callback = null)
    {
        _callback = callback;
        _textCount = 0;
        iconX.gameObject.SetActive( true );
        iconCheck.gameObject.SetActive( false );
        transform.localPosition = new Vector3( 0, 0, -500f );
    }

    void Update()
    {
        _textString = _inputTextField.text;

        if (CheckValidName(_textString) == false)
        {
            iconCheck.gameObject.SetActive( false );
            iconX.gameObject.SetActive( true );
        } else
        {
            iconCheck.gameObject.SetActive( true );
            iconX.gameObject.SetActive( false );
        }
        if( _pastString.Equals( _inputField.text ) == false )
        {
            _pastString = _inputField.text;
            string tx = _inputField.text; 
            string keyName = string.Empty;
            for( int i = 0; i < tx.Length; i++ )
            {
                if( ( '0' <= tx[i] && '9' >= tx[i] ) ||
                    ( 'a' <= tx[i] && 'z' >= tx[i] ) ||
                    ( 'A' <= tx[i] && 'Z' >= tx[i] ) ||
                    ( '가' <= tx[i] && '힣' >= tx[i] ) )
                {
                    keyName = string.Format( "{0}{1}", keyName, tx[i] );
                }
            }

            if( null != _inputTextField )
                _inputTextField.text = keyName;
        }
    }

    bool CheckValidName(string textName)
    {
        if (textName.Length < 2 || textName.Length > 8)
            return false;

        //Issue 5 - 한글 완성형 문제: 완성형이 아닌 캐릭터가 매칭이 되면 false를 리턴한다. 한글과 영문 조합은 현재로서는 가능하다. (2016-05-23)
        //위의 업데이트 문에서 _pastString처리로 완성형 체크를 하는 것 같은데, RegEx로 처리를 하면 좀 더 나을듯 하다.
        var rex = new Regex(@"[ㄱ-ㅎㅏ-ㅣ]");
        if (rex.IsMatch(textName))
            return false;

        return true;
    }

