    void SetTeamNameCallBack( JSONObject jsonObj )
    {
        int code = (int)jsonObj["_Code"].n;

        if ( code == (int)common.ResultCode.OK )
        {
            DataManager.instance.netdata.player._TeamName = _inputTextField.text;
            Debug.Log( "Set TeamName : " + _inputTextField.text );
            if ( _callback != null )
            {
                _callback();
            }
            this.transform.gameObject.SetActive( false );
        }
        else if(code == (int)common.ResultCode.INVALID_PARAMETER)
        {
            if (_textString.Length < 2)
            {
                //UIManager.instance.OpenPopup( E_UIPOPUP_TYPE.CheckPopup ).GetComponent<CheckPopup>().SetCheckPopupDesc( E_CHECK_POPUP_TYPE.Confirm, "안내", " 팀 이름이 너무 짧아요!" );
                UIManager.instance.OpenCheckPopup(E_CHECK_POPUP_TYPE.Confirm, E_LOCALIZE_STRING.SYSTEM_MESSAGE_DESC, E_LOCALIZE_STRING.SYSTEM_ALERT_TEAM_NAME_TOOSHORT_DESC);
            }
            else if (_textString.Length > 7)
            {
                //UIManager.instance.OpenPopup(E_UIPOPUP_TYPE.CheckPopup).GetComponent<CheckPopup>().SetCheckPopupDesc(E_CHECK_POPUP_TYPE.Confirm, "안내", " 팀 이름이 너무 길어요!");
                UIManager.instance.OpenCheckPopup(E_CHECK_POPUP_TYPE.Confirm, E_LOCALIZE_STRING.SYSTEM_MESSAGE_DESC, E_LOCALIZE_STRING.SYSTEM_ALERT_TEAM_NAME_TOOLONG_DESC);
            }
        }
        else if ( code == (int)common.ResultCode.ALREADY_EXISTS )
        {

            if ( _textString.Length < 2 )
            {
                //UIManager.instance.OpenPopup( E_UIPOPUP_TYPE.CheckPopup ).GetComponent<CheckPopup>().SetCheckPopupDesc( E_CHECK_POPUP_TYPE.Confirm, "안내", " 팀 이름이 너무 짧아요!" );
                UIManager.instance.OpenCheckPopup( E_CHECK_POPUP_TYPE.Confirm, E_LOCALIZE_STRING.SYSTEM_MESSAGE_DESC, E_LOCALIZE_STRING.SYSTEM_ALERT_TEAM_NAME_TOOSHORT_DESC );
            }
            else if ( _textString.Length > 7 )
            {
                //UIManager.instance.OpenPopup(E_UIPOPUP_TYPE.CheckPopup).GetComponent<CheckPopup>().SetCheckPopupDesc(E_CHECK_POPUP_TYPE.Confirm, "안내", " 팀 이름이 너무 길어요!");
                UIManager.instance.OpenCheckPopup( E_CHECK_POPUP_TYPE.Confirm, E_LOCALIZE_STRING.SYSTEM_MESSAGE_DESC, E_LOCALIZE_STRING.SYSTEM_ALERT_TEAM_NAME_TOOLONG_DESC );
            }
            else
            {
                //UIManager.instance.OpenPopup( E_UIPOPUP_TYPE.CheckPopup ).GetComponent<CheckPopup>().SetCheckPopupDesc( E_CHECK_POPUP_TYPE.Confirm, "안내", " 사용중인 팀이름입니다." );
                UIManager.instance.OpenCheckPopup( E_CHECK_POPUP_TYPE.Confirm, E_LOCALIZE_STRING.SYSTEM_MESSAGE_DESC, E_LOCALIZE_STRING.SYSTEM_ALERT_TEAM_NAME_ALREADY_USE_DESC );
            }
            _inputTextField.text = string.Empty;
            
            if ( null == _keyboard )
                return;

            if ( true == _keyboard.done )
            {
                _keyboard.text = string.Empty;
            }
        }
        else if(code == 98)
        {
            UIManager.instance.OpenCheckPopup( E_CHECK_POPUP_TYPE.Confirm, "안내" , "부적절한 팀 이름 입니다." );
        }
    }


